import { useState } from 'react'

export default function Accordion(props) {
  const [expandedIndex, setExpandedIndex] = useState(0)

  const { items } = props
  const renderedItems = items.map((item, index) => {
    const isExpanded = index === expandedIndex
    const handleClick = (index) => {

      setExpandedIndex((currentExpandedIndex) => {
      // if the item is already open, close it, else open it
      if (currentExpandedIndex === nextIndex) {
        return -1
      }
        else {
          return nextIndex
        }
      })
    }
    return (
    <div key={item.id}>
        <div
        onClick={()=>handleClick(nextIndex)}
        className="flex justify-between p-3 bg=gray-100 border-b items-center cursor-pointer"
        >
          {item.label}
        </div>
        {/* If the content index matches the expanded index in state (isExpanded is true), render it, otherwise render nothing*/}
        {isExpanded && <div>{item.content}</div>}
    </div>
  )
})

  return <div>{renderedItems}</div>
}
