import ReactDOM from 'react-dom';
import { useEffect, useState } from 'react';

const Modal = ({ children, isOpen, onClose }) => {
  const [modalRoot, setModalRoot] = useState(null);

  useEffect(() => {
    const root = document.getElementById('modal-root');
    if (root) {
      setModalRoot(root);
    }
  }, []);

  if (!isOpen || !modalRoot) {
    return null; // If the modal is not open or modal-root is not found, return null
  }

  return ReactDOM.createPortal(
    <div
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, 0.5)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        zIndex: 1000, // Ensure it is on top
      }}
      onClick={onClose}
    >
      <div
        style={{
          backgroundColor: '#fff',
          padding: '20px',
          borderRadius: '10px',
          maxWidth: '500px',
          width: '100%',
        }}
        onClick={(e) => e.stopPropagation()} // Prevent click from closing modal
      >
        {children}
        <button
          style={{
            marginTop: '20px',
            backgroundColor: '#f44336',
            color: '#fff',
            padding: '10px 15px',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            display: 'block',
            marginLeft: 'auto',
            marginRight: 'auto',
          }}
          onClick={onClose}
        >
          Close Modal
        </button>
      </div>
    </div>,
    modalRoot
  );
};

export default Modal;
